import classes from './Counter.module.css';

import {useSelector} from 'react-redux'
import Button from '../../../../05-allthingslearnedtillnowproject/01-starting-project (1)/01-starting-project/src/components/Button';



const Counter = () => {
  const toggleCounterHandler = () => {};

  //so when we use the useSelector 
  //react-redux automatically 
  //instantiates a subscriptioin to the stores part tthat we are extracting here
  const counter = useSelector(state => state.counter);


  //dispatching actions





  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>{counter}</div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>

      Button

    </main>
  );
};

export default Counter;
